bc = {'';
'';
'';
'';
''};

startingNumber = 5;

currentNumber = startingNumber;

nDigits = length(bc);
bathroomCode = cell(nDigits,1);

for n = 1:nDigits
    currentCode = bc{n};
    for j = 1:length(currentCode)
        currentNumber = keypadmove2(currentNumber, currentCode(j));
    end
    startingNumber = currentNumber;
    bathroomCode{n} = currentNumber;
end
display(bathroomCode);